import{_ as e,c as r,o as a,ag as d}from"./chunks/framework.B-XtCDNB.js";const c=JSON.parse('{"title":"API Reference","description":"","frontmatter":{},"headers":[],"relativePath":"api/reference.md","filePath":"api/reference.md"}'),s={name:"api/reference.md"};function o(i,t,n,h,l,u){return a(),r("div",null,[...t[0]||(t[0]=[d('<h1 id="api-reference" tabindex="-1">API Reference <a class="header-anchor" href="#api-reference" aria-label="Permalink to &quot;API Reference&quot;">​</a></h1><h2 id="common-attribute-reference" tabindex="-1">Common Attribute Reference <a class="header-anchor" href="#common-attribute-reference" aria-label="Permalink to &quot;Common Attribute Reference&quot;">​</a></h2><p>The following are core attributes that you will reach for commonly.</p><table tabindex="0"><thead><tr><th>Attribute</th><th>Description</th></tr></thead><tbody><tr><td><a href="./attributes/request.html">data-request</a></td><td>makes a request to a given AJAX handler</td></tr><tr><td><a href="./attributes/request-data.html">data-request-data</a></td><td>supplies postback data with the request</td></tr><tr><td><a href="./attributes/request-query.html">data-request-query</a></td><td>supplies query data with the request and modifies the URL</td></tr><tr><td><a href="./attributes/request-trigger.html">data-request-trigger</a></td><td>the event that triggers the request</td></tr><tr><td><a href="./attributes/request-poll.html">data-request-poll</a></td><td>automatically triggers the request at a regular interval</td></tr><tr><td><a href="./attributes/request-update.html">data-request-update</a></td><td>request specified partial view updates</td></tr></tbody></table><h3 id="additional-attribute-reference" tabindex="-1">Additional Attribute Reference <a class="header-anchor" href="#additional-attribute-reference" aria-label="Permalink to &quot;Additional Attribute Reference&quot;">​</a></h3><p>All other attributes are specified below.</p><table tabindex="0"><thead><tr><th>Attribute</th><th>Description</th></tr></thead><tbody><tr><td><a href="./attributes/request-url.html">data-request-url</a></td><td>specifies a custom URL endpoint for the AJAX request.</td></tr><tr><td><a href="./attributes/request-confirm.html">data-request-confirm</a></td><td>displays a confirmation message before sending the request.</td></tr><tr><td><a href="./attributes/request-before-send.html">data-request-before-send</a></td><td>execute JavaScript before the request is sent.</td></tr><tr><td><a href="./attributes/request-before-update.html">data-request-before-update</a></td><td>execute JavaScript before the page contents are updated.</td></tr><tr><td><a href="./attributes/request-success.html">data-request-success</a></td><td>execute JavaScript on a successful request.</td></tr><tr><td><a href="./attributes/request-error.html">data-request-error</a></td><td>execute JavaScript on a failed request.</td></tr><tr><td><a href="./attributes/request-complete.html">data-request-complete</a></td><td>execute JavaScript on every completed request.</td></tr><tr><td><a href="./attributes/request-cancel.html">data-request-cancel</a></td><td>execute JavaScript if the user aborts the request or cancels it via a confirmation dialog.</td></tr><tr><td><a href="./attributes/request-redirect.html">data-request-redirect</a></td><td>specifies a URL to redirect the browser after the successful AJAX request.</td></tr><tr><td><a href="./attributes/request-message.html">data-request-message</a></td><td>displays a progress message with the specified text, shown while the request is running.</td></tr><tr><td><a href="./attributes/request-files.html">data-request-files</a></td><td>when specified the request will accept file uploads using the <code>FormData</code> interface.</td></tr><tr><td><a href="./attributes/request-flash.html">data-request-flash</a></td><td>instructs the server to send flash messages with the response.</td></tr><tr><td><a href="./attributes/request-validate.html">data-request-validate</a></td><td>enables inline form validation with error display.</td></tr><tr><td><a href="./attributes/request-loading.html">data-request-loading</a></td><td>shows/hides an element while the request is running.</td></tr><tr><td><a href="./attributes/attach-loading.html">data-attach-loading</a></td><td>disables the element and shows a loading indicator during requests.</td></tr></tbody></table><h2 id="available-events" tabindex="-1">Available Events <a class="header-anchor" href="#available-events" aria-label="Permalink to &quot;Available Events&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Event</th><th>Target</th><th>Description</th></tr></thead><tbody><tr><td><a href="./events/#ajax:setup">ajax:setup</a></td><td>trigger</td><td>before a request is formed.</td></tr><tr><td><a href="./events/#ajax:before-request">ajax:before-request</a></td><td>trigger</td><td>fired before the request is sent.</td></tr><tr><td><a href="./events/#ajax-before-send">ajax:before-send</a></td><td>window</td><td>fired before sending a request.</td></tr><tr><td><a href="./events/#ajax:request-start">ajax:request-start</a></td><td>window</td><td>fired when the HTTP request starts.</td></tr><tr><td><a href="./events/#ajax:request-end">ajax:request-end</a></td><td>window</td><td>fired when the HTTP request ends.</td></tr><tr><td><a href="./events/#ajax:before-update">ajax:before-update</a></td><td>trigger</td><td>fired after the request is complete, but before the page is updated.</td></tr><tr><td><a href="./events/#ajax:update">ajax:update</a></td><td>element</td><td>fired after the element has updated.</td></tr><tr><td><a href="./events/#ajax:update-complete">ajax:update-complete</a></td><td>window</td><td>fired after all elements have updated.</td></tr><tr><td><a href="./events/#ajax:request-success">ajax:request-success</a></td><td>window</td><td>request has completed successfully.</td></tr><tr><td><a href="./events/#ajax:request-complete">ajax:request-complete</a></td><td>trigger</td><td>fired after the request completes (success or error).</td></tr><tr><td><a href="./events/#ajax:request-cancel">ajax:request-cancel</a></td><td>trigger</td><td>fired when the request is cancelled (e.g., confirm declined).</td></tr><tr><td><a href="./events/#ajax:request-error">ajax:request-error</a></td><td>window</td><td>request encountered an error.</td></tr><tr><td><a href="./events/#ajax:error-message">ajax:error-message</a></td><td>window</td><td>an error message wants to display.</td></tr><tr><td><a href="./events/#ajax:confirm-message">ajax:confirm-message</a></td><td>window</td><td>a confirm message wants to display.</td></tr></tbody></table><h2 id="javascript-api-reference" tabindex="-1">JavaScript API Reference <a class="header-anchor" href="#javascript-api-reference" aria-label="Permalink to &quot;JavaScript API Reference&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Method</th><th>Description</th></tr></thead><tbody><tr><td><a href="./framework/#ajax">jax.ajax()</a></td><td>issues an AJAX request</td></tr><tr><td><a href="./framework/#request">jax.request()</a></td><td>issues an AJAX request with a form element</td></tr><tr><td><a href="./framework/#flash-msg">jax.flashMsg()</a></td><td>displays a flash message</td></tr><tr><td><a href="./framework/#progress-bar">jax.progressBar()</a></td><td>displays the progress bar</td></tr><tr><td><a href="./framework/#attach-loader">jax.attachLoader()</a></td><td>flags an element as loading</td></tr><tr><td><a href="./framework/#page-ready">jax.pageReady()</a></td><td>waits for the page to be ready</td></tr><tr><td><a href="./framework/#wait-for">jax.waitFor()</a></td><td>waits for a an object to exist</td></tr><tr><td><a href="./framework/#dispatch">jax.dispatch()</a></td><td>triggers a global event</td></tr><tr><td><a href="./framework/#trigger">jax.trigger()</a></td><td>triggers an event on an element</td></tr><tr><td><a href="./framework/#on">jax.on()</a></td><td>attaches an event handler to an element</td></tr><tr><td><a href="./framework/#off">jax.off()</a></td><td>removes an event handler from an element</td></tr><tr><td><a href="./framework/#one">jax.one()</a></td><td>attaches a one-time event handler that removes itself after firing</td></tr><tr><td><a href="./framework/#values">jax.values()</a></td><td>extracts the input values from a container element.</td></tr></tbody></table><h3 id="turbo-router-functions" tabindex="-1">Turbo Router Functions <a class="header-anchor" href="#turbo-router-functions" aria-label="Permalink to &quot;Turbo Router Functions&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Method</th><th>Description</th></tr></thead><tbody><tr><td><a href="./framework/#use-turbo">jax.useTurbo()</a></td><td>returns true if turbo routing is enabled</td></tr><tr><td><a href="./framework/#visit">jax.visit()</a></td><td>updates the page location</td></tr></tbody></table><h3 id="hot-control-functions" tabindex="-1">Hot Control Functions <a class="header-anchor" href="#hot-control-functions" aria-label="Permalink to &quot;Hot Control Functions&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Method</th><th>Description</th></tr></thead><tbody><tr><td><a href="./framework/#register-control">jax.registerControl()</a></td><td>registers a new hot control</td></tr><tr><td><a href="./framework/#import-control">jax.importControl()</a></td><td>returns a registered hot control class definition</td></tr><tr><td><a href="./framework/#observe-control">jax.observeControl()</a></td><td>attach a hot control and return the instance</td></tr><tr><td><a href="./framework/#fetch-control">jax.fetchControl()</a></td><td>fetch an instance on a single element</td></tr><tr><td><a href="./framework/#fetch-controls">jax.fetchControls()</a></td><td>fetch instances on a multiple elements</td></tr></tbody></table><h2 id="available-response-methods" tabindex="-1">Available Response Methods <a class="header-anchor" href="#available-response-methods" aria-label="Permalink to &quot;Available Response Methods&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Method</th><th>Description</th></tr></thead><tbody><tr><td><a href="./response/#update">update()</a></td><td>adds DOM updates to the AJAX response</td></tr><tr><td><a href="./response/#data">data()</a></td><td>adds response data to the response</td></tr><tr><td><a href="./response/#redirect">redirect()</a></td><td>adds a browser redirect to the response</td></tr><tr><td><a href="./response/#reload">reload()</a></td><td>adds a browser refresh command to the response</td></tr><tr><td><a href="./response/#flash">flash()</a></td><td>adds flash messages to the response</td></tr><tr><td><a href="./response/#error">error()</a></td><td>adds an error message to the response</td></tr><tr><td><a href="./response/#fatal">fatal()</a></td><td>adds a fatal error message to the response</td></tr><tr><td><a href="./response/#js">js()</a></td><td>adds a JavaScript file or files to load with the output</td></tr><tr><td><a href="./response/#css">css()</a></td><td>adds a StyleSheet file or files to load with the output</td></tr><tr><td><a href="./response/#img">img()</a></td><td>adds an image file or files to load with the output</td></tr><tr><td><a href="./response/#browserEvent">browserEvent()</a></td><td>adds browser event dispatch with the response</td></tr><tr><td><a href="./response/#browserEventAsync">browserEventAsync()</a></td><td>adds asynchronous browser event dispatch with the response</td></tr><tr><td><a href="./response/#invalidFields">invalidFields()</a></td><td>adds invalid form fields to the response</td></tr><tr><td><a href="./response/#partials">partials()</a></td><td>provides multiple requested partial responses to the browser</td></tr><tr><td><a href="./response/#exception">exception()</a></td><td>processes an exception as an response</td></tr></tbody></table><h3 id="available-override-methods" tabindex="-1">Available Override Methods <a class="header-anchor" href="#available-override-methods" aria-label="Permalink to &quot;Available Override Methods&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Method</th><th>Description</th></tr></thead><tbody><tr><td><a href="./response/#register-custom-response">registerCustomResponse()</a></td><td>replaces the <code>AjaxResponse</code> class with a custom one</td></tr><tr><td><a href="./response/#register-global-component">registerGlobalComponent()</a></td><td>register a stateless component class available globally</td></tr></tbody></table>',19)])])}const p=e(s,[["render",o]]);export{c as __pageData,p as default};
